import { Rllista } from './Rllista';
import { useState } from 'react';
import { persones, competidors, administradors, headerConfig } from "../models/instancies.js";

export function Container() {
  // Desestructuració d'arrays
  let [titol, setTitol] = useState("Titol");
  let [capçalera, setCapçalera] = useState([]);
  let [datos, setDatos] = useState([]);

  function removeElem() {
    datos.splice(0, 1);
    setDatos([...datos]);
  }
  function addCapçalera() {
      capçalera.push(headerConfig);
      setCapçalera([...datos]);
  }
  function addPersona() {
      datos.push(persones);
      setDatos([...datos]);
  }
  function addCompetidor() {
      datos.push(competidors);
      setDatos([...datos]);
  }
  function addAdministrador() {
      datos.push(administradors);
      setDatos([...datos]);
  }


  function canviTitol() {
    let tt = prompt("Nou titol:")
    if (tt) {
      setTitol(tt);
    //document.getElementById("titol").innerHTML = tt;
  }
  }

  return (
    <>
      <h1>{titol}</h1>
      <h1>Llista</h1>
      <div>
        <Rllista  data={datos} />
        <button  onClick={canviTitol}>Capçalera</button>
        <button  onClick={addCapçalera}>Capçalera</button>
        <button  onClick={(addPersona)}>Persona</button>
        <button  onClick={addCompetidor}>Competidor</button>
        <button  onClick={(addAdministrador)}>Administrador</button>
        <button  onClick={addAdministrador}>Ordenar</button>
        <button  onClick={addPersona}>Filtrar</button>
        <button  onClick={(removeElem)}>Ordenar</button>
      </div>
    </>
  );
}
