import { Rllista } from "./Rllista";
import { useState } from "react";
import {
  persones,
  competidors,
  administradors,
  headerConfig,
} from "../models/instancies.js";

export function Container() {
  // Desestructuració d'arrays
  let [titol, setTitol] = useState("Titol");
  let [headerConf, setCapçalera] = useState([]);
  let [datos, setDatos] = useState([]);
  let copiaDatos = [];

  function removeElem() {
    datos.splice(0, 1);
    setDatos([...datos]);
  }
  function addCapçalera() {
    headerConfig.forEach((capçalera) => headerConf.push(capçalera));
    setCapçalera([...headerConf]);
  }

  function addPersona() {
    persones.forEach((persona) => datos.push(persona));
    setDatos([...datos]);
  }
  function addCompetidor() {
    competidors.forEach((competidor) => datos.push(competidor));
    setDatos([...datos]);
  }
  function addAdministrador() {
    administradors.forEach((administrador) => datos.push(administrador));
    setDatos([...datos]);
  }

  function canviTitol() {
    let tt = prompt("Nou titol:");
    if (tt) {
      setTitol(tt);
      //document.getElementById("titol").innerHTML = tt;
    }
  }

  function Ordenar(){
    copiaDatos = datos.slice().sort(ordenarNom);
    setDatos([...copiaDatos]);
  }
  function ordenarNom(a, b) {
    return a.nom > b.nom;
  }
  function Filtrar(){
    copiaDatos = datos.filter((elem) => elem.alçada > 170);
    setDatos([...copiaDatos]);
  }
  function Totalitzar(){

    const totalElement = this._shadowRoot.getElementById("totalitzar");
    let resultat;
    let totalitzarAlçada = this._llistaOriginal.map((ele) => ele[camp]);

    resultat = totalitzarAlçada.reduce((acum, valor) => acum + valor, 0);
    totalElement.innerHTML = `<p>Altura Total: ${resultat}</p>`;
  }

  return (
    <>
      <h1>{titol}</h1>
      <h1>Llista</h1>
      <div>
        <Rllista data={datos} header={headerConf} />
        <button onClick={canviTitol}>Títol</button>
        <button onClick={addCapçalera}>Capçalera</button>
        <button onClick={addPersona}>Persona</button>
        <button onClick={addCompetidor}>Competidor</button>
        <button onClick={addAdministrador}>Administrador</button>
        <button onClick={Ordenar}>Ordenar</button>
        <button onClick={Filtrar}>Filtrar</button>
        <button onClick={Totalitzar}>Totalitzar</button>
      </div>
    </>
  );
}
