  Persona.prototype.renderRow = function (camps) {
    return camps.map((camp) => (this[camp]) ? `<td>${this[camp]}</td>` : "<td></td>").join(" ");
  };

  return (
    <table>
      <thead>
        <tr>
          {props.header.map((item) => (
            <th>{item.header}</th>
          ))}
        </tr>
      </thead>
      <tbody>
      {props.data.map(item => (                  
      <StringToJSX myString={item.renderRow(props.header.map((conf)=> conf.header))} />
                      ))}

      </tbody>
    </table>
  );
}
