export function Rllista(props) {
  //   de forma iterativa
  //   let r = [];
  //     for (let item of props.data){
  //         r.push(
  //         <tr>
  //             <td>{item}</td>
  //         </tr>
  //         )
  //     }
  //     return <table>
  //         {r}
  //     </table>
  const renderRow = function (camps) {
    return camps.map((camp) => (this[camp]) ? `<td>${this[camp]}</td>` : "<td></td>").join(" ");
  };

  return (
    <table>
      <thead>
        <tr>
          {props.header.map((item) => (
            <th>{item.header}</th>
          ))}
        </tr>
      </thead>
      <tbody>

      {props.data.forEach((elem) => (
        <tr>{elem.renderRow(props.header)}</tr>
      ))}


        props.data.map((item) => (
          <tr>
            {Object.values(item).map((value) => (
                <td>{value}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
}
