export function Rllista(props) {
  //   de forma iterativa
  //   let r = [];
  //     for (let item of props.data){
  //         r.push(
  //         <tr>
  //             <td>{item}</td>
  //         </tr>
  //         )
  //     }
  //     return <table>
  //         {r}
  //     </table>

  const renderRow = function (camps) {
    return camps.map((camp) => ([camp]) ? <td>{[camp]}</td> : <td></td>)
  };

  return (
    <table>
      <thead>
        <tr>
          {props.header.map((item) => (
            <th>{item.header}</th>
          ))}
        </tr>
      </thead>
      <tbody>

      {props.data.map((elem) => (
        <tr>{elem.renderRow(props.header)}</tr>
      ))}

      </tbody>
    </table>
  );
}
