export function Rllista(props) {
  //   de forma iterativa
  //   let r = [];
  //     for (let item of props.data){
  //         r.push(
  //         <tr>
  //             <td>{item}</td>
  //         </tr>
  //         )
  //     }
  //     return <table>
  //         {r}
  //     </table>
  const renderRow = (item, fields) => {
    return (
      <tr>
        {fields.map((field) => (
          <td key={field}>{item[field] || ''}</td>
        ))}
      </tr>
    );
  };

  return (
    <table>
      <thead>
        <tr>
          {props.header.map((item) => (
            <th>{item.header}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {props.data.map((item) => (
          <tr>
            {Object.values(item).map((value) => (
                <td>{value}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
}
